package main
import "fmt"

func main() {
    var competidores int
    fmt.Scan(&competidores)

    if competidores < 1 || competidores > 1000 {
        return
    }

    var linhas int
    fmt.Scan(&linhas)

    var a, b, indice int
    var menor int = 101
    indice = -1

    fmt.Scan(&a, &b)

    if a >= 10 && b >= 10 {
        if a > b {
            var t int = b
            b = a
            a = t
        }
        menor = b - a
        indice = 0
    }

    for i := 1; i < linhas; i++ {
        var x, y int
        fmt.Scan(&x, &y)

        if x >= 10 && y >= 10 {
            if x > y {
                var v int = y
                y = x
                x = v
            }

            if indice == -1 || b-a > y-x {
                b = y
                a = x
                indice = i
            }
        }
    }

    if indice != -1 {
        fmt.Println(indice)
    }
}
